mapper.select:
SELECT SUM(connectorHisPower.power) power,DATE_FORMAT(connectorHisPower.in_time, '%H:%i:%S') remark_1
from exc_connector_history_power_info connectorHisPower, exc_station_info stationInfo
where connectorHisPower.station_id=stationInfo.station_id and connectorHisPower.operator_id=stationInfo.operator_id
and
 connectorHisPower.in_time >=CURRENT_TIMESTAMP - INTERVAL 1 MINUTE 

  and stationInfo.station_id=#{stationID}


  and stationInfo.operator_id = #{operatorID}


  and stationInfo.area_code = #{areaCode}


  and stationInfo.area_code in

    #{areaCode}


GROUP BY connectorHisPower.in_time

select power,current_a,current_b,current_c,voltage_a,voltage_b,voltage_c,in_time
from exc_connector_history_power_info
where
 in_time >=CURRENT_TIMESTAMP - INTERVAL 30 MINUTE 

  and operator_id = #{operatorID}


  and equipment_id = #{equipmentID}


  and station_id=#{stationId}

ORDER BY in_time desc

select power,current_a,current_b,current_c,voltage_a,voltage_b,voltage_c,in_time
from exc_connector_history_power_info
where 1=1

   and in_time > #{lastEndDate} 


  and operator_id = #{operatorID}


  and equipment_id = #{equipmentID}


  and station_id=#{stationId}

ORDER BY in_time desc

SELECT ROUND(IFNULL(SUM(connectorHisPower.power) ,0)/count(connectorHisPower.id),2) power
from exc_connector_history_power_info connectorHisPower, exc_station_info stationInfo
where connectorHisPower.`status`in (3,5) and connectorHisPower.station_id=stationInfo.station_id and connectorHisPower.operator_id=stationInfo.operator_id
and
 connectorHisPower.in_time >=CURRENT_TIMESTAMP - INTERVAL 1 MINUTE 

  and stationInfo.station_id=#{stationID}


  and stationInfo.operator_id = #{operatorID}


  and stationInfo.area_code = #{areaCode}


  and connectorHisPower.equipment_id = #{equipmentOwnerID}


  and stationInfo.area_code in

    #{areaCode}

SELECT sum(power) power,DATE_FORMAT(received_time, '%H:%i:%S') remark_1
from exc_connector_history_power_info
where 1=1

  and station_id=#{stationId}


  and equipment_id=#{equipmentId}


  and operator_id=#{operatorId}

ORDER BY received_time DESC

select DATE_FORMAT(CONCAT(CURDATE(),' ',quarterTime.quarter_time),'%Y-%m-%d %H:%i:%s') in_time,
sum(IFNULL(a.power_sum,0.0)) power ,sum(IFNULL(a.rated_power_sum,0.0)) charge_electricity from
quarter_time quarterTime LEFT JOIN
(
select a.back_time ,ROUND(IFNULL(sum(b.power)/count(b.id),0.0),2) power_sum,c.power rated_power_sum from
(select DATE_FORMAT(CONCAT(CURDATE(),' ',quarter_time),'%Y-%m-%d %H:%i:%s') back_time,
date_sub(DATE_FORMAT(CONCAT(CURDATE(),' ',quarter_time),'%Y-%m-%d %H:%i:%s'),interval 15 minute) front_time
from quarter_time
where DATE_FORMAT(CONCAT(CURDATE(),' ',quarter_time),'%Y-%m-%d %H:%i:%s') <=NOW()
)a , exc_connector_history_power_info b ,exc_equipment_info c,exc_station_info d
where b.`status`in (3,5) and b.in_time<=a.back_time and b.in_time>=a.front_time
and c.equipment_id=b.equipment_id and c.operator_id=b.operator_id  and d.station_id=b.station_id and b.operator_id=d.operator_id
 /*快充*/
  and c.power >=7

 /*慢充 小于7*/
  and c.power <7


  and d.area_code in

    #{areaCode}



  and c.sid in

    #{sid}



  and b.operator_id in

      #{operatorId}



  and b.equipment_id in

      #{equipmentID}



  and d.station_street in

      #{street}


  GROUP BY b.equipment_id,b.operator_id

) a on a.back_time= DATE_FORMAT(CONCAT(CURDATE(),' ',quarterTime.quarter_time),'%Y-%m-%d %H:%i:%s')
where DATE_FORMAT(CONCAT(CURDATE(),' ',quarterTime.quarter_time),'%Y-%m-%d %H:%i:%s') <=NOW()
GROUP BY DATE_FORMAT(CONCAT(CURDATE(),' ',quarterTime.quarter_time),'%Y-%m-%d %H:%i:%s')

SELECT

from  exc_connector_history_power_info
where 1=1

   and in_time >= #{beginTime} 


   and in_time <= #{endTime} 


  and equipment_id=#{equipmentID}


  and operator_id=#{operatorID}


  and station_id=#{stationId}


  and connector_id=#{connectorId}
