mapper.select:
select

from exc_allowance_equipment
where 1=1

  and id =#{id,jdbcType=VARCHAR}


  and batch_id=#{batchId,jdbcType=VARCHAR}


  and sid=#{sid,jdbcType=VARCHAR}


  and eid=#{eid,jdbcType=VARCHAR}


  and if_reduced=#{ifReduced,jdbcType=INTEGER}


  and reduced_type=#{reducedType,jdbcType=INTEGER}

select distinct sid
from exc_allowance_equipment
where if_reduced=1 and batch_id = #{batchId,jdbcType=VARCHAR}

select distinct eid
from exc_allowance_equipment
where if_reduced=1 and batch_id = #{batchId,jdbcType=VARCHAR}
