mapper.select:
SELECT bms_code, operator_id, station_id, equipment_id, connector_id, SUM(charge_times_sum) AS statisticalTimes, statistical_time FROM `sec_battery_collect_month_charge`
bms_code = #{param.bMSCode} AND statistical_time BETWEEN DATE_FORMAT( #{param.startTime}, '%Y%m%' ) AND DATE_FORMAT( #{param.endTime}, '%Y%m' )
AND operator_id = #{param.operatorId}
AND station_id = #{param.stationId}
AND equipment_id = #{param.equipmentId}
GROUP BY bms_code, operator_id, station_id, equipment_id, connector_id, statistical_time ORDER BY statistical_time ASC

select b.operator_id, b.station_id, b.equipment_id, a.bms_code, b.sOH_last, b.remain_capacity_last, b.esti_r_last, a.sOH_sum, a.remain_capacity_sum, a.charge_times_sum, a.charge_time_sum, a.charge_time_f, a.charge_time_s, a.tatal_voltage_h, a.total_current_h, a.voltage_h, a.voltage_l, a.tempture_h, a.tempture_l, a.before_soc_l, a.before_soc_sum, a.after_soc_h, a.after_soc_sum, a.end_time from (SELECT bms_code, SUM(sOH_sum) as sOH_sum, SUM(remain_capacity_sum) as remain_capacity_sum, SUM(charge_times_sum) as charge_times_sum, SUM(charge_time_sum) as charge_time_sum, MIN(charge_time_f) as charge_time_f, MAX(charge_time_s) as charge_time_s, MAX(tatal_voltage_h) as tatal_voltage_h, MAX(total_current_h) as total_current_h, MAX(voltage_h) as voltage_h, MIN(voltage_l) as voltage_l, MAX(tempture_h) as tempture_h, MIN(tempture_l) as tempture_l, MIN(before_soc_l) as before_soc_l, SUM(before_soc_sum) as before_soc_sum, MAX(after_soc_h) as after_soc_h, SUM(after_soc_sum) as after_soc_sum, MAX(end_time) as end_time FROM `sec_battery_collect_month_charge` WHERE statistical_time =#{statisticalTime} and bms_code=#{bMSCode}
GROUP BY
operator_id
,station_id
,equipment_id
) as a , sec_battery_collect_month_charge b where a.end_time = b.end_time and a.bms_code= b.bms_code

  	SELECT
		 warning_code,
		 SUM(warning_code_times) as warning_code_times
	FROM
	 `sec_battery_day_warning_result`
	WHERE
	  	statistical_time = #{statisticalTime}
	  	AND bms_code = #{bMSCode}

			AND operator_id = #{operatorId}


			AND	station_id = #{stationId}	


			AND equipment_id = #{equipmentId}

	GROUP BY
	  warning_code

	 ,operator_id


	 ,station_id


	 ,equipment_id

  	SELECT
		 warning_level,
		 SUM(warning_code_times) as warning_code_times
	FROM
	 `sec_battery_day_warning_result`
	WHERE
	  	    statistical_time = #{statisticalTime}
	  	    AND bms_code = #{bMSCode}

			AND operator_id = #{operatorId}


			AND	station_id = #{stationId}	


			AND equipment_id = #{equipmentId}

	GROUP BY
	  warning_level

	 ,operator_id


	 ,station_id


	 ,equipment_id

  	SELECT
			operator_id,
			station_id,
			equipment_id,

			bms_code,
			statistical_time
		FROM
			sec_battery_collect_month_charge

				statistical_time =#{param.statisticalTime}

				AND bms_code = #{param.bMSCode}


				AND operator_id = #{param.operatorId}


				AND	station_id = #{param.stationId}	


				AND equipment_id = #{param.equipmentId}


			AND bms_code is not null and bms_code !='' and bms_code !='0' and bms_code !='无' and bms_code !='暂无'
			ORDER BY statistical_time DESC
