mapper.select:
SELECT operator_id, station_id, equipment_id,
bms_code, end_time FROM sec_battery_single_charge
bms_code = #{param.bMSCode}
AND DATE_FORMAT(end_time, '%Y-%m-%d %H') BETWEEN DATE_FORMAT( #{param.startTime}, '%Y-%m-%d %H' ) AND DATE_FORMAT( #{param.endTime}, '%Y-%m-%d %H' )
AND DATE_FORMAT(end_time, '%Y-%m-%d') BETWEEN DATE_FORMAT( #{param.startTime}, '%Y-%m-%d' ) AND DATE_FORMAT( #{param.endTime}, '%Y-%m-%d' )
AND DATE_FORMAT(end_time, '%Y-%m') BETWEEN DATE_FORMAT( #{param.startTime}, '%Y-%m' ) AND DATE_FORMAT( #{param.endTime}, '%Y-%m' )
AND DATE_FORMAT(end_time, '%Y-%m') BETWEEN DATE_FORMAT( #{param.startTime}, '%Y-%m' ) AND DATE_FORMAT( #{param.endTime}, '%Y-%m' )
AND DATE_FORMAT(end_time, '%Y') BETWEEN DATE_FORMAT( #{param.startTime}, '%Y' ) AND DATE_FORMAT( #{param.endTime}, '%Y' )
AND operator_id = #{param.operatorId}
AND station_id = #{param.stationId}
AND equipment_id = #{param.equipmentId}
AND bms_code is not null and bms_code !='' and bms_code !='0' and bms_code !='无' and bms_code !='暂无' ORDER BY end_time DESC

SELECT bms_code, operator_id, station_id, equipment_id, connector_id, COUNT(bms_code) AS statisticalTimes, end_time as statisticalTime FROM sec_battery_single_charge
bms_code = #{param.bMSCode} AND DATE_FORMAT(end_time, '%Y-%m-%d %H') BETWEEN DATE_FORMAT( #{param.startTime}, '%Y-%m-%d %H' ) AND DATE_FORMAT( #{param.endTime}, '%Y-%m-%d %H' )
AND operator_id = #{param.operatorId}
AND station_id = #{param.stationId}
AND equipment_id = #{param.equipmentId}
GROUP BY bms_code, operator_id, station_id, equipment_id, connector_id, DATE_FORMAT(end_time, '%Y-%m-%d %H') ORDER BY DATE_FORMAT(end_time, '%Y-%m-%d %H') ASC

SELECT sOH FROM sec_battery_single_charge
bms_code = #{param.bMSCode,jdbcType=VARCHAR}
AND operator_id = #{param.operatorId}
AND station_id = #{param.stationId}
AND equipment_id = #{param.equipmentId}
AND DATE_FORMAT(end_time, '%Y%m') = DATE_FORMAT(#{param.time},'%Y%m')
AND DATE_FORMAT(end_time, '%Y%m') >= DATE_FORMAT( #{param.startTime}, '%Y%m' ) AND DATE_FORMAT(end_time, '%Y%m') <= DATE_FORMAT( #{param.endTime}, '%Y%m' )
AND DATE_FORMAT(end_time, '%Y') = DATE_FORMAT(#{param.time},'%Y')
AND sOH IS NOT NULL
